# add to utils.R
.parse_Currency <- function(vals) {
    res <- gsub("^\\[|\\]$", "",
        "[UNK, USD, GBP, CAD, EUR, AUD, NZD, SEK, ILS, CHF, HKD, JPY, SGD, KRW, NOK, DKK, PLN, RON, HUF, CZK, BRL, MXN, ARS, CLP, COP]"
    )
    unlist(strsplit(res, ", "))
}

